import { mapGetAllEventsQueryEvents } from "../../mapGetAllEventsQueryEvents";
import { SubgraphQueryHandler, } from "../subgraphQueryHandler";
import { FlowUpdatedEventsDocument, } from "./events.generated";
export class FlowUpdatedEventQueryHandler extends SubgraphQueryHandler {
    constructor() {
        super(...arguments);
        this.getAddressFieldKeysFromFilter = () => ({
            accountKeys: ["sender", "receiver", "flowOperator"],
            tokenKeys: ["token"],
        });
        this.requestDocument = FlowUpdatedEventsDocument;
    }
    getRelevantAddressesFromResultCore(result) {
        return {
            accounts: [result.sender, result.receiver],
            tokens: [result.token],
        };
    }
    mapFromSubgraphResponse(response) {
        return mapGetAllEventsQueryEvents(response.flowUpdatedEvents);
    }
}
